import React, { useEffect, useState } from 'react'

function Animal({chosenAnimal, correctAnswer}) {
return (
    <div className="word">
      {chosenAnimal.split('').map((letter, i) => {
        return (
          <span className="letter" key={i}>
            {correctAnswer.includes(letter) ? letter : ''}
          </span>
        )
      })}
    </div>
  )

}

export default Animal
