          window.alert("gagné")
          break;
      }
      if (number == 3)
      switch (player) {
        case 1:
          window.alert("gagné")
          break;
        case 2:
          window.alert("perdu")
          break;
        case 3:
          window.alert("égalité")
          break;
      }
  }

  return (
    <div className='App-header'>
      <Button onClick={play} />
    </div>
  )
}


export default App;
