const GOOD_LETTER = "AZERTYUIOPQSDFGHJKLMWXCVBNazertyuiopqsdfghjklmwxcvbnéàâêè";
function CallApi() {
    const [word, setWord] = useState(undefined);
    useEffect(() => {
        (async () => {
            const { data } = await getWord();
            setWord(data);
            console.log(data);
        })();
        return () => { };
    }, []
    );
    
    const getWord = async () => {
        const dataJson = await fetch('https://animalfinderapi.herokuapp.com/word');
        return await dataJson.json();
    };


    const handleKeyDown = (event) => {
        if (GOOD_LETTER.includes(event.key)) {
            console.log("A key was pressed", event.key);
            if (actualWord.includes(event.key)) {
                console.log("correct");
            } else console.log("wrong",);
        }
    };

    useEffect(() => {
        window.addEventListener("keydown", handleKeyDown, false);
        return () => {
            window.removeEventListener("keydown", handleKeyDown);
        };
    }, []);


    if (!word) {
        return <p>Waiting...</p>
    }

    const actualWord = word.word;

    return (
        <div>
            <p>{actualWord}</p>
        </div>
    )

}

export default CallApi
