import React, { useEffect, useState } from 'react'

function Animal({correct}) {
    

if(!word){
    return <p>Waiting...</p>
}

return (
    <div className="word">
      {word.word.split('').map((letter, i) => {
        return (
          <span className="letter" key={i}>
            {correct.includes(letter) ? letter : ''}
          </span>
        )
      })}
    </div>
  )

}

export default Animal
